(()=>{"use strict";const e=document.getElementById("city"),t=document.getElementById("curr-temp"),n=document.getElementById("max-temp"),m=document.getElementById("min-temp");!async function(){const r=await async function(e){try{const t=await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${e}&APPID=d9db3cd9136b599d1cb981aab399f6fe`,{mode:"cors"}).then((e=>{if(e.ok)return e;throw new Error("City Not Found")})),n=await t.json();return await async function(e){const t=273.15;return{currTemp:Number((e.temp-t).toFixed(0)),maxTemp:Number((e.temp_max-t).toFixed(0)),minTemp:Number((e.temp_min-t).toFixed(0))}}(n.main)}catch(e){alert(e)}}("Cape Town");e.innerHTML="Cape Town",t.innerHTML=`Current Temperature: ${r.currTemp} °C`,n.innerHTML=`Maximum Temperature: ${r.maxTemp} °C`,m.innerHTML=`Minimum Temperature: ${r.minTemp} °C`}()})();