(()=>{"use strict";const e=document.getElementById("search-input"),t=document.getElementById("search-button"),n=document.getElementById("city"),r=document.getElementById("curr-temp"),m=document.getElementById("max-temp"),a=document.getElementById("min-temp");async function i(e){try{n.innerHTML="",r.innerHTML="",m.innerHTML="",a.innerHTML="",n.innerHTML="Loading...";const t=await async function(e){try{const t=await fetch(`http://api.openweathermap.org/data/2.5/weather?q=${e}&APPID=d9db3cd9136b599d1cb981aab399f6fe`,{mode:"cors"}).then((e=>{if(e.ok)return e;throw new Error("City Not Found")})),n=await t.json();return await async function(e){const t=273.15;return{currTemp:Number((e.temp-t).toFixed(0)),maxTemp:Number((e.temp_max-t).toFixed(0)),minTemp:Number((e.temp_min-t).toFixed(0))}}(n.main)}catch(e){throw e}}(e);n.innerHTML=e,r.innerHTML=`Current Temperature: ${t.currTemp} °C`,m.innerHTML=`Maximum Temperature: ${t.maxTemp} °C`,a.innerHTML=`Minimum Temperature: ${t.minTemp} °C`}catch(e){alert(e)}}t.addEventListener("click",(()=>{i(e.value)}))})();